export function validateUsername(username) {
    if (!username || username.length < 3) {
        return '用户名至少需要3个字符';
    }

    if (username.length > 20) {
        return '用户名不能超过20个字符';
    }

    if (!/^[a-zA-Z0-9_-]+$/.test(username)) {
        return '用户名只能包含字母、数字、下划线和连字符';
    }

    return null;
}

