export function validateForm(formData, fields) {
    const errors = {};
    let isValid = true;

    for (const field of fields) {
        const error = validateField(formData[field.name], field.rules);
        if (error) {
            errors[field.name] = error;
            isValid = false;
        }
    }

    return { isValid, errors };
}

