        }
    }

    return { isValid, errors };
}

export function formatUserDisplayName(user) {
    if (!user) return '未知用户';

    if (user.firstName && user.lastName) {
        return `${user.firstName} ${user.lastName}`;
    }

    return user.username || user.email || '未知用户';
}

