export function secureStorageSet(key, value) {
    try {
        const data = {
            value: value,
            timestamp: Date.now(),
            checksum: btoa(JSON.stringify(value)) // 简单的完整性检查
        };
        localStorage.setItem(key, JSON.stringify(data));
    } catch (error) {
        console.warn('存储失败:', error);
    }
}

