    async refreshToken() {
        try {
            const refreshToken = secureStorageGet('refresh_token');
            if (!refreshToken) {
                throw new Error('没有刷新令牌');
            }

            const response = await httpClient.post(this.endpoints.refresh, {
                refreshToken: refreshToken
            });

            if (response.data.success) {
