            // 发送全局事件
            if (typeof window !== 'undefined') {
                window.dispatchEvent(new CustomEvent('auth:logout'));
            }
        }

clearErrors({ commit }) {
            commit('CLEAR_ERRORS');
        }

setRedirectPath({ commit }, path) {
