export default {
  name: 'UserProfile',

  data() {
    return {
      // 编辑状态
      editMode: false,

      // 编辑表单
      editForm: {
        firstName: '',
        lastName: '',
        email: ''
      },

      // 表单验证错误
      errors: {},

      // 密码修改
      showChangePassword: false,
      passwordForm: {
        currentPassword: '',
        newPassword: '',
        confirmNewPassword: ''
      },
      passwordErrors: {},
      isPasswordLoading: false,

      // 默认头像
      defaultAvatar: '/images/default-avatar.png',

      // 登录统计（模拟数据）
      loginStats: {
        totalLogins: 156,
        consecutiveDays: 7
      }
    };
  },

  computed: {
    ...mapState('auth', [
      'user',
      'isLoading'
    ]),

    ...mapGetters('auth', [
      'userDisplayName',
      'userPermissions',
      'userRole'
    ]),

    // 角色显示名称
    roleDisplayName() {
      const roleMap = {
        'admin': '管理员',
        'user': '普通用户',
        'moderator': '版主',
        'guest': '访客'
      };
      return roleMap[this.userRole] || this.userRole;
    },

    // 账户年龄
    accountAge() {
      if (!this.user.createdAt) return '未知';

      const created = new Date(this.user.createdAt);
      const now = new Date();
      const diffInDays = Math.floor((now - created) / (1000 * 60 * 60 * 24));

      if (diffInDays < 30) return `${diffInDays} 天`;
      if (diffInDays < 365) return `${Math.floor(diffInDays / 30)} 个月`;
      return `${Math.floor(diffInDays / 365)} 年`;
    },

    // 编辑表单是否有效
    isFormValid() {
      return this.editForm.firstName &&
             this.editForm.lastName &&
             this.editForm.email &&
             Object.keys(this.errors).length === 0;
    },

    // 密码表单是否有效
    isPasswordFormValid() {
      return this.passwordForm.currentPassword &&
             this.passwordForm.newPassword &&
             this.passwordForm.confirmNewPassword &&
             Object.keys(this.passwordErrors).length === 0;
    }
  },

  watch: {
    // 监听用户数据变化，初始化编辑表单
    user: {
      handler(newUser) {
        if (newUser) {
          this.initEditForm();
        }
      },
      immediate: true
    }
  },

  methods: {
    ...mapActions('auth', [
      'updateProfile',
      'changePassword'
    ]),

    // 初始化编辑表单
    initEditForm() {
      if (this.user) {
        this.editForm = {
          firstName: this.user.firstName || '',
          lastName: this.user.lastName || '',
          email: this.user.email || ''
        };
      }
    },

    // 更新用户资料
    async updateProfile() {
      if (!this.validateEditForm()) {
        return;
      }

      try {
        const result = await this.$store.dispatch('auth/updateProfile', this.editForm);

        if (result.success) {
          this.editMode = false;
          this.$message({
            type: 'success',
            message: '资料更新成功！'
          });
        } else {
          this.$message({
            type: 'error',
            message: result.message || '更新失败'
          });
        }
      } catch (error) {
        this.$message({
          type: 'error',
          message: '更新过程中发生错误'
        });
      }
    },

    // 验证编辑表单
    validateEditForm() {
      this.errors = {};

      if (!this.editForm.firstName) {
        this.errors.firstName = '姓不能为空';
      }

      if (!this.editForm.lastName) {
        this.errors.lastName = '名不能为空';
      }

      if (!this.editForm.email) {
        this.errors.email = '邮箱不能为空';
      } else if (!validateEmail(this.editForm.email)) {
        this.errors.email = '邮箱格式不正确';
      }

      return Object.keys(this.errors).length === 0;
    },

    // 验证单个字段
    validateField(fieldName) {
      const value = this.editForm[fieldName];

      switch (fieldName) {
        case 'firstName':
        case 'lastName':
          if (!value || !value.trim()) {
            this.$set(this.errors, fieldName, '此字段不能为空');
          } else {
            this.$delete(this.errors, fieldName);
          }
          break;
        case 'email':
          if (!value) {
            this.$set(this.errors, fieldName, '邮箱不能为空');
          } else if (!validateEmail(value)) {
            this.$set(this.errors, fieldName, '邮箱格式不正确');
          } else {
            this.$delete(this.errors, fieldName);
          }
          break;
      }
    },

    // 取消编辑
    cancelEdit() {
      this.editMode = false;
      this.initEditForm();
      this.errors = {};
    },

    // 头像上传
    triggerAvatarUpload() {
      this.$refs.avatarInput.click();
    },

    handleAvatarUpload(event) {
      const file = event.target.files[0];
      if (!file) return;

      // 验证文件类型和大小
      if (!file.type.startsWith('image/')) {
        this.$message({
          type: 'error',
          message: '请选择图片文件'
        });
        return;
      }

      if (file.size > 2 * 1024 * 1024) { // 2MB
        this.$message({
          type: 'error',
          message: '图片大小不能超过2MB'
        });
        return;
      }

      // 这里应该上传到服务器
      // 暂时使用本地预览
      const reader = new FileReader();
      reader.onload = (e) => {
        // 更新用户头像（实际应该调用API）
        this.$message({
          type: 'success',
          message: '头像上传成功！'
        });
      };
      reader.readAsDataURL(file);
    },

    // 修改密码
    async changePassword() {
      if (!this.validatePasswordForm()) {
        return;
      }

      this.isPasswordLoading = true;

      try {
        const result = await this.$store.dispatch('auth/changePassword', {
          currentPassword: this.passwordForm.currentPassword,
          newPassword: this.passwordForm.newPassword
        });

        if (result.success) {
          this.closePasswordModal();
          this.$message({
            type: 'success',
            message: '密码修改成功！'
          });
        } else {
          this.$message({
            type: 'error',
            message: result.message || '密码修改失败'
          });
        }
      } catch (error) {
        this.$message({
          type: 'error',
          message: '修改密码过程中发生错误'
        });
      } finally {
        this.isPasswordLoading = false;
      }
    },

    // 验证密码表单
    validatePasswordForm() {
      this.passwordErrors = {};

      if (!this.passwordForm.currentPassword) {
        this.passwordErrors.currentPassword = '请输入当前密码';
      }

      if (!this.passwordForm.newPassword) {
        this.passwordErrors.newPassword = '请输入新密码';
      } else {
        const error = validatePassword(this.passwordForm.newPassword);
        if (error) {
          this.passwordErrors.newPassword = error;
        }
      }

      if (!this.passwordForm.confirmNewPassword) {
        this.passwordErrors.confirmNewPassword = '请确认新密码';
      } else if (this.passwordForm.newPassword !== this.passwordForm.confirmNewPassword) {
        this.passwordErrors.confirmNewPassword = '两次输入的密码不一致';
      }

      return Object.keys(this.passwordErrors).length === 0;
    },

    // 关闭密码修改模态框
    closePasswordModal() {
      this.showChangePassword = false;
      this.passwordForm = {
        currentPassword: '',
        newPassword: '',
        confirmNewPassword: ''
      };
      this.passwordErrors = {};
    },

    // 格式化日期
    formatDate(date) {
      if (!date) return '未知';
      return new Date(date).toLocaleDateString('zh-CN');
    },

    // 格式化最后登录时间
    formatLastLogin
  }
};

