          { required: true },
          { minLength: 1 },
          { maxLength: 50 }
        ],
        password: [
          { required: true },
          { minLength: 6 }
        ],
        confirmPassword: [
          { required: true },
          { 
            custom: (value) => {
              return value === this.registerForm.password || '两次输入的密码不一致';
            }
          }
        ],
