          {
            custom: (value) => {
              return value === true || '请先同意用户协议和隐私政策';
            }
          }
        ]
      }
    };
  },

  computed: {
    ...mapState('auth', [
      'isLoading',
      'registerError'
    ]),

    // 表单是否有效
    isFormValid() {
      const formFilled = this.registerForm.username && 
                        this.registerForm.email && 
                        this.registerForm.firstName &&
                        this.registerForm.lastName &&
                        this.registerForm.password && 
                        this.registerForm.confirmPassword &&
                        this.registerForm.agreeToTerms;

      return formFilled && Object.keys(this.errors).length === 0;
    },

    // 密码强度
    passwordStrength() {
      const password = this.registerForm.password;
      if (!password) return 0;

      let strength = 0;

      // 长度检查
      if (password.length >= 6) strength += 1;
      if (password.length >= 8) strength += 1;

      // 复杂度检查
      if (/[a-z]/.test(password)) strength += 1;
      if (/[A-Z]/.test(password)) strength += 1;
      if (/[0-9]/.test(password)) strength += 1;
      if (/[^a-zA-Z0-9]/.test(password)) strength += 1;

      return Math.min(strength, 4);
    },

    passwordStrengthText() {
      const texts = ['', '弱', '一般', '强', '很强'];
      return texts[this.passwordStrength] || '';
    },

    passwordStrengthClass() {
      const classes = ['', 'weak', 'fair', 'good', 'strong'];
      return classes[this.passwordStrength] || '';
    },

    passwordStrengthWidth() {
      return `${(this.passwordStrength / 4) * 100}%`;
    }
  },

  methods: {
    ...mapActions('auth', [
      'register',
      'clearErrors'
    ]),

    // 处理注册提交
    async handleRegister() {
      // 验证表单
      if (!this.validateForm()) {
        return;
      }

      try {
        const result = await this.register(this.registerForm);

        if (result.success) {
          this.registerSuccess = true;
          // 3秒后跳转到登录页面
          setTimeout(() => {
            this.showLogin();
          }, 3000);
        }
      } catch (error) {
        console.error('注册失败:', error);
      }
    },

    // 验证整个表单
    validateForm() {
      this.errors = {};
      let isValid = true;

      // 遍历所有字段进行验证
      Object.keys(this.validationRules).forEach(fieldName => {
        const error = this.validateSingleField(fieldName);
        if (error) {
          this.errors[fieldName] = error;
          isValid = false;
        }
      });

      return isValid;
    },

    // 验证单个字段
    validateField(fieldName) {
      const error = this.validateSingleField(fieldName);
      if (error) {
        this.$set(this.errors, fieldName, error);
      } else {
        this.$delete(this.errors, fieldName);
      }
    },

    // 验证单个字段的具体逻辑
    validateSingleField(fieldName) {
      const value = this.registerForm[fieldName];
      const rules = this.validationRules[fieldName];

      if (!rules) return null;

      for (const rule of rules) {
        // 必填验证
        if (rule.required && (!value || value.toString().trim() === '')) {
          return '此字段为必填项';
        }

        // 跳过空值的其他验证
        if (!value) continue;

        // 最小长度验证
        if (rule.minLength && value.length < rule.minLength) {
          return `最少需要${rule.minLength}个字符`;
        }

        // 最大长度验证
        if (rule.maxLength && value.length > rule.maxLength) {
          return `最多只能${rule.maxLength}个字符`;
        }

        // 邮箱格式验证
        if (rule.email && !validateEmail(value)) {
          return '请输入有效的邮箱地址';
        }

        // 正则表达式验证
        if (rule.pattern && !rule.pattern.test(value)) {
          if (fieldName === 'username') {
            return '用户名只能包含字母、数字、下划线和连字符';
          }
          return '格式不正确';
        }

        // 自定义验证
        if (rule.custom) {
          const result = rule.custom(value);
          if (result !== true) {
            return typeof result === 'string' ? result : '验证失败';
          }
        }
      }

      return null;
    },

    // 清除字段错误
    clearFieldError(fieldName) {
      if (this.errors[fieldName]) {
        this.$delete(this.errors, fieldName);
      }

      // 清除全局错误
      this.clearErrors();
    },

    // 显示登录页面
    showLogin() {
      this.$emit('show-login');
    },

    // 显示用户协议
    showTerms() {
      // 打开用户协议模态框或页面
      window.open('/terms', '_blank');
    },

    // 显示隐私政策
    showPrivacy() {
      // 打开隐私政策模态框或页面
      window.open('/privacy', '_blank');
    },

    // 重置表单
    resetForm() {
      this.registerForm = {
        username: '',
        email: '',
        firstName: '',
        lastName: '',
        password: '',
        confirmPassword: '',
        agreeToTerms: false
      };
      this.errors = {};
      this.registerSuccess = false;
      this.showPassword = false;
      this.showConfirmPassword = false;
    }
  },

  // 组件销毁时清理
  beforeDestroy() {
    this.clearErrors();
  }
};

