      // 验证表单
      if (!this.validateForm()) {
        return;
      }

      try {
        const result = await this.login(this.loginForm);

        if (!result.success) {
          // 登录失败，可能需要显示验证码
          if (this.loginAttempts >= 2) {
            this.showCaptcha = true;
            this.refreshCaptcha();
          }
        }
      } catch (error) {
        console.error('登录失败:', error);
      }
    },

    // 验证整个表单
    validateForm() {
      this.errors = {};
      let isValid = true;

      // 验证用户名
      const usernameError = validateUsername(this.loginForm.username);
      if (usernameError) {
        this.errors.username = usernameError;
        isValid = false;
      }

      // 验证密码
      const passwordError = validatePassword(this.loginForm.password);
      if (passwordError) {
        this.errors.password = passwordError;
        isValid = false;
      }

      // 验证验证码
      if (this.showCaptcha && !this.loginForm.captcha) {
        this.errors.captcha = '请输入验证码';
        isValid = false;
      }

      return isValid;
    },

    // 验证单个字段
    validateField(fieldName) {
      const value = this.loginForm[fieldName];
      const rules = this.validationRules[fieldName];

      if (rules) {
        const error = validateField(value, rules);
        if (error) {
          this.$set(this.errors, fieldName, error);
        } else {
          this.$delete(this.errors, fieldName);
        }
      }
    },

    // 清除字段错误
    clearFieldError(fieldName) {
      if (this.errors[fieldName]) {
        this.$delete(this.errors, fieldName);
      }
    },

    // 切换密码显示
    togglePassword() {
      this.showPassword = !this.showPassword;
    },

    // 刷新验证码
    refreshCaptcha() {
      this.captchaUrl = `/api/captcha?t=${Date.now()}`;
      this.loginForm.captcha = '';
    },

    // 显示忘记密码
    showForgotPassword() {
      this.$emit('show-forgot-password');
    },

    // 显示注册
    showRegister() {
      this.$emit('show-register');
    },

    // 社交登录
    handleSocialLogin(provider) {
      // 跳转到社交登录页面
      window.location.href = `/api/auth/social/${provider.name}`;
    },

    // 登录成功处理
    handleLoginSuccess() {
      this.$emit('login-success');

      // 显示成功消息
      this.$message({
        type: 'success',
        message: '登录成功！'
      });

      // 清空表单
      this.resetForm();
    },

    // 重置表单
    resetForm() {
      this.loginForm = {
        username: '',
        password: '',
        rememberMe: false,
        captcha: ''
      };
      this.errors = {};
      this.showPassword = false;
    },

    // 清除所有错误
    clearAllErrors() {
      this.errors = {};
      this.clearErrors();
    }
  }
